mapper.select:
select A.USERNAME, I.NAME, L.LOG_ID, L.INER_TYPE, L.INPUT, L.OUTPUT, L.USER_IP, L.USER_ID, L.CALL_DATE, L.CODE, L.REQ_NO from SYS_RECYCLE_LOG L LEFT JOIN SYS_APP_USER A ON A.USER_ID = L.USER_ID LEFT JOIN SYS_INTERFACE I ON I.TYPE = L.INER_TYPE where 1=1
and( L.USER_ID LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or L.USER_IP LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or L.INER_TYPE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or L.CODE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or L.REQ_NO LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and L.CALL_DATE >= #{pd.lastLoginStart}
and L.CALL_DATE <= #{pd.lastLoginEnd}
order by L.CALL_DATE DESC
